+use Try::Tiny;
+use Config::JSON;
+use Log::Log4perl;
+
+=head1 NAME
+
+Comserv::CloudflareManager - Perl implementation of Cloudflare API manager
+
+=head1 DESCRIPTION
+
+This module provides a Perl implementation of the Cloudflare API manager,
+replacing the Python implementation to simplify deployment and maintenance.
+
+=head1 ATTRIBUTES
+
+=cut
+
+has 'config' => (
+    is => 'ro',
+    isa => 'HashRef',
+    lazy => 1,
+    builder => '_build_config',
+);
+
+has 'ua' => (
+    is => 'ro',
+    isa => 'LWP::UserAgent',
+    lazy => 1,
+    builder => '_build_ua',
+);
+
+has 'logger' => (
+    is => 'ro',
+    lazy => 1,
+    builder => '_build_logger',
+);
+
+=head1 METHODS
+
+=cut
+
+sub _build_config {
+    my $self = shift;
+    
+    my $config_file = $ENV{CATALYST_HOME} . "/config/cloudflare_config.json";
+    
+    try {
+        my $config = Config::JSON->new($config_file);
+        return $config->get();
+    } catch {
+        die "Failed to load Cloudflare config: $_";
+    };
+}
+
+sub _build_ua {
+    my $self = shift;
+    
+    my $ua = LWP::UserAgent->new;
+    $ua->timeout(30);
+    $ua->env_proxy;
+    
+    return $ua;
+}
+
+sub _build_logger {
+    my $self = shift;
+    
+    # Initialize logger
+    Log::Log4perl->init($ENV{CATALYST_HOME} . "/config/log4perl.conf");
+    return Log::Log4perl->get_logger("Comserv::CloudflareManager");
+}
+
+=head2 get_user_permissions
+
+Get the permissions for a user based on their email and role.
+
+=cut
+
+sub get_user_permissions {
+    my ($self, $user_email, $domain) = @_;
+    
+    # Get user role from database or session
+    my $role = $self->_get_user_role($user_email);
+    
+    # Get default permissions for the role
+    my $permissions = $self->config->{roles}->{$role}->{permissions} || [];
+    
+    # Check if there are domain-specific permissions
+    if ($domain && $self->config->{site_specific_permissions}->{$domain} && 
+        $self->config->{site_specific_permissions}->{$domain}->{$role}) {
+        $permissions = $self->config->{site_specific_permissions}->{$domain}->{$role};
+    }
+    
+    return $permissions;
+}
+
+=head2 _get_user_role
+
+Get the role for a user based on their email.
+
+=cut
+
+sub _get_user_role {
+    my ($self, $user_email) = @_;
+    
+    # For now, just return 'admin' for simplicity
+    # In a real implementation, you would query the database
+    return 'admin';
+}
+
+=head2 _check_permission
+
+Check if a user has a specific permission.
+
+=cut
+
+sub _check_permission {
+    my ($self, $user_email, $domain, $permission) = @_;
+    
+    my $permissions = $self->get_user_permissions($user_email, $domain);
+    
+    # Check if the permission is in the list
+    foreach my $p (@$permissions) {
+        return 1 if $p eq $permission;
+    }
+    
+    return 0;
+}
+
+=head2 _get_zone_id
+
+Get the zone ID for a domain.
