+        # Test if the table actually exists by attempting a simple operation
+        # This will trigger the "table doesn't exist" error if needed
+        $rs->result_source->schema->storage->dbh_do(sub {
+            my ($storage, $dbh) = @_;
+            my $table_name = $rs->result_source->name;
+            $dbh->prepare("SELECT 1 FROM `$table_name` LIMIT 0")->execute();
+        });
+        
+    } catch {
+        my $error = $_;
+        if ($error =~ /Table.*doesn't exist/i) {
+            # Extract table name from the source name (convert to lowercase for consistency)
+            my $table_name = lc($source_name);
+            
+            # Get the Catalyst context from the current request
+            # This is a bit tricky since we're in a model, but we can try to get it
+            my $c;
+            eval {
+                # Try to get the current Catalyst context
+                $c = $self->catalyst_component_name ? 
+                     $self->catalyst_component_name->context : 
+                     undef;
+            };
+            
+            # If we can't get the context, we'll need to handle this differently
+            if (!$c) {
+                # Log the error and re-throw - we can't sync without context
+                warn "DBEncy: Table '$table_name' doesn't exist and no Catalyst context available for sync";
+                die $error;
+            }
+            
+            # Attempt to sync the missing table using HybridDB
+            my $hybrid_db = $c->model('HybridDB');
