+                # Table sync successful, retry getting the resultset
+                $rs = $self->SUPER::resultset($source_name);
+            } else {
+                # Sync failed, re-throw the original error
+                die $error;
+            }
+        } else {
+            # Non-table error, re-throw
+            die $error;
+        }
+    };
+    
+    return $rs;
+}
