+#!/usr/bin/perl
+use strict;
+use warnings;
+use DBI;
+
+# Try to connect directly to MySQL database
+my $dbh;
+eval {
+    $dbh = DBI->connect(
+        'dbi:mysql:database=comserv;host=localhost',
+        'root',
+        '',
+        { RaiseError => 1, PrintError => 0, mysql_enable_utf8 => 1 }
+    );
+};
+
+if ($@) {
+    print "Database connection failed: $@\n";
+    
+    # Try alternative connection strings
+    print "Trying alternative database connections...\n";
+    
+    # Try with different database names
+    my @db_names = ('comserv', 'comserv2', 'ency');
+    my @hosts = ('localhost', '127.0.0.1');
+    
+    foreach my $db_name (@db_names) {
+        foreach my $host (@hosts) {
+            eval {
+                $dbh = DBI->connect(
+                    "dbi:mysql:database=$db_name;host=$host",
+                    'root',
+                    '',
+                    { RaiseError => 1, PrintError => 0, mysql_enable_utf8 => 1 }
+                );
+                print "Successfully connected to database '$db_name' on host '$host'\n";
+                last;
+            };
+            if (!$@) { last; }
+        }
+        if (!$@) { last; }
+    }
+    
+    if ($@) {
+        print "All database connection attempts failed. Last error: $@\n";
+        exit 1;
+    }
+}
+
+print "Database connection successful!\n";
+
+# Check what tables exist
+my $sth = $dbh->prepare("SHOW TABLES");
+$sth->execute();
+my @tables = ();
