+    push @tables, $table;
+}
+print "Available tables: " . join(", ", @tables) . "\n";
+
+# Check if projects table exists
+if (grep { $_ eq 'projects' } @tables) {
+    print "\nProjects table found!\n";
+    
+    # Get count of projects
+    my $count_sth = $dbh->prepare("SELECT COUNT(*) FROM projects");
+    $count_sth->execute();
+    my ($count) = $count_sth->fetchrow_array();
