+        my $sitenames_sth = $dbh->prepare("SELECT DISTINCT sitename FROM projects");
+        $sitenames_sth->execute();
+        while (my ($sitename) = $sitenames_sth->fetchrow_array()) {
+            print "SiteName: '" . ($sitename || 'NULL') . "'\n";
+        }
+        
+        # Show top-level projects
+        print "\nTop-level projects (parent_id is NULL or empty):\n";
