+#!/usr/bin/env perl
+
+use strict;
+use warnings;
+use FindBin;
+use lib "$FindBin::Bin/Comserv/lib";
+use DBI;
+use Config::General;
+
+# Load configuration
+my $config_file = "$FindBin::Bin/Comserv/comserv.conf";
+
+if (!-f $config_file) {
+    print "Config file not found at: $config_file\n";
+    print "Looking for alternative config files...\n";
+    
+    # Try to find config files
+    my @possible_configs = (
+        "$FindBin::Bin/Comserv/comserv.conf",
+        "$FindBin::Bin/comserv.conf",
+        "$FindBin::Bin/Comserv/config/comserv.conf"
+    );
+    
+    foreach my $conf (@possible_configs) {
+        if (-f $conf) {
+            $config_file = $conf;
+            print "Found config file: $config_file\n";
+            last;
+        }
+    }
+    
+    if (!-f $config_file) {
+        print "No config file found. Trying default database connection...\n";
+        
+        # Try default connections
+        my @default_connections = (
+            ['dbi:mysql:database=comserv;host=localhost', 'root', ''],
+            ['dbi:mysql:database=comserv;host=localhost', 'comserv', ''],
+            ['dbi:mysql:database=ency;host=localhost', 'root', ''],
+            ['dbi:SQLite:dbname=comserv.db', '', '']
+        );
+        
+        foreach my $conn (@default_connections) {
+            my ($dsn, $user, $pass) = @$conn;
+            print "Trying connection: $dsn with user: $user\n";
+            
+            eval {
+                my $dbh = DBI->connect($dsn, $user, $pass, { RaiseError => 1, PrintError => 0 });
+                if ($dbh) {
+                    print "Successfully connected!\n";
+                    &check_projects_table($dbh);
+                    $dbh->disconnect();
+                    exit 0;
+                }
+            };
+            if ($@) {
+                print "Connection failed: $@\n";
+            }
+        }
+        
+        print "All connection attempts failed.\n";
+        exit 1;
+    }
+}
+
+# Try to read config
+my %config;
+eval {
+    %config = Config::General->new($config_file)->getall;
+};
+
+if ($@) {
+    print "Error reading config file: $@\n";
+    exit 1;
+}
+
+print "Config loaded successfully from: $config_file\n";
+
+# Get database connection info
+my $db_info = $config{'Model::DB'}{connect_info} || $config{'Model::DBEncy'}{connect_info};
+
+if (!$db_info) {
+    print "No database connection info found in config.\n";
+    print "Config keys: " . join(", ", keys %config) . "\n";
+    exit 1;
+}
+
+my $dsn = $db_info->[0];
+my $user = $db_info->[1];
+my $pass = $db_info->[2];
+
+print "Connecting to database...\n";
+print "DSN: $dsn\n";
+print "User: $user\n";
+
+# Connect to the database
+my $dbh;
+eval {
+    $dbh = DBI->connect($dsn, $user, $pass, { RaiseError => 1, AutoCommit => 1 });
+};
+
+if ($@) {
+    print "Database connection failed: $@\n";
+    exit 1;
+}
+
+print "Database connection successful!\n";
+
+&check_projects_table($dbh);
+
+$dbh->disconnect();
+
+sub check_projects_table {
+    my ($dbh) = @_;
+    
+    # Check what tables exist
