# Create the icons
foreach my $icon_name (keys %icon_templates) {
    my $filename = "$icon_name.svg";
    open my $fh, '>', $filename or die "Could not open file '$filename': $!";
    
    # Add XML header
    print $fh "<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n";
    
    # Add the SVG content
    print $fh $icon_templates{$icon_name};
    
    close $fh;
    print "Created $filename\n";
}

# Generate CSS entries
print "\nCSS entries for svg-icons.css:\n\n";
foreach my $icon_name (keys %icon_templates) {
    print ".icon-$icon_name, .fas.fa-$icon_name {\n";
    print "  -webkit-mask-image: url('/static/images/icons/$icon_name.svg');\n";
    print "  mask-image: url('/static/images/icons/$icon_name.svg');\n";
    print "  background-color: currentColor;\n";
    print "  background-image: none;\n";
    print "}\n\n";
}

print "Script completed successfully.\n";